let num1=document.getElementById("num1"),num2=document.getElementById("num2"),enviar=document.getElementById("enviar"),resultado=document.getElementById("resultado");enviar.addEventListener("click",(e=>{e.preventDefault();let t=num1.value,n=num2.value,m=new FormData;m.append("num1",t),m.append("num2",n),fetch("https://api.malcotti.com/suma/",{method:"post",body:m}).then((e=>e.text())).then((e=>{resultado.innerHTML=e})).catch((e=>{}))}));